import React, { Component } from 'react'

export default class Classbase extends Component {
  constructor(){
    super()

  this.state={
    alpha:'be',
    count:'always'}
  }

  HandlerClick(){
    this.setState({
      alpha:'beyoond_apply',
      count:'optional+menage'
    })
  }

  render(){
    return (
      <div>
        <h1 >Always be Happy {this.state.alpha} {this.state.count} </h1>
        <button onClick={()=> this.HandlerClick()}>click Me </button>
      </div>
    )
  }
}
