import React, { Component } from 'react'

export default class Classbase extends Component{
  state={
    alpha:'a',
    count:'always'}

  HandlerClick=()=>{
    this.setState({
      alpha:'beyoond_apply',
      count:'optional+menage'
    })
  }

  render(){
    return (
      <div>
        <h1 >{this.state.alpha} Always be Happy {this.state.count} </h1>
        <button onClick={()=> this.HandlerClick()}>click Me </button>
      </div>
    )
  }
}
